INSERT INTO `form_astro_genre` (`id`, `texte`, `trigger`, `sexe`, `signe`, `conjoint`) VALUES
(1, 'dynamique, avec un temp&eacute;rament fougueux qui l''entraine au coeur de l''action, il a tendance &agrave; foncer t&ecirc;te baiss&eacute;e dans tout ce qu''il entreprend. Ambitieux, il a un r&eacute;el besoin d''agir dans son quotidien, ce qui ne l''emp&ecirc;che pas de faire preuve d''une grande g&eacute;n&eacute;rosit&eacute;. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours', 'question_2', 'H', 'belier', '1'),
(2, 'simple, sinc&egrave;re et g&eacute;n&eacute;reux qui aime la vie et en profite au maximum. Bien qu''il prenne le temps de la r&eacute;flexion avant de se lancer dans des projets, lorsqu''il a pris conscience du bien fond&eacute; de ceux-ci, rien ne peut l''arr&ecirc;ter et il ne changera pas d''avis. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours', 'question_2', 'H', 'taureau', '1'),
(3, 'observateur et compr&eacute;hensif qui est motiv&eacute; par une r&eacute;elle curiosit&eacute; d''apprendre. Son esprit ne s''arr&ecirc;te jamais de travailler et il est important qu''il y ait de l''activit&eacute; dans son quotidien afin qu''il ne puisse pas rester plong&eacute; dans ses pens&eacute;es et se poser trop de questions. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'gemeaux', '1'),
(4, 'imaginatif, &eacute;motif avec une nature de r&ecirc;veur, qui a tendance &agrave; vivre dans le pass&eacute; et &agrave; se le ressasser. Cette nostalgie du pass&eacute; l''am&egrave;ne &agrave; rechercher un milieu s&ucirc;r et o&ugrave; il se sent en s&eacute;curit&eacute;, ce qui ne l''emp&ecirc;che pas de faire preuve d''une bonne humeur communicative. Mais c''est &eacute;galement un homme sensible bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'cancer', '1'),
(5, 'd&eacute;termin&eacute;, confiant et d&eacute;cid&eacute;, qui est &eacute;cout&eacute; et respect&eacute;, et qui est pr&eacute;destin&eacute; &agrave; accomplir de grandes choses. Il met toutes les chances de son c&ocirc;t&eacute; afin de r&eacute;aliser ses r&ecirc;ves gr&acirc;ce notamment &agrave; son audace et &agrave; son charisme naturel. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours', 'question_2', 'H', 'lion', '1'),
(6, 'perfectionniste, raisonnable et pr&eacute;voyant, qui analyse beaucoup ce qui se d&eacute;roule autour de lui. Il aime que le travail soit bien fait et n''a de cesse d''essayer d''optimiser les choses en laissant un peu &agrave; l''&eacute;cart la spontan&eacute;it&eacute;. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'vierge', '1'),
(7, 'perspicace, aimable, avec une tol&eacute;rance naturelle qui fait de lui une personne qui plait en soci&eacute;t&eacute; du fait qu''il s''adapte facilement. Malgr&eacute; un caract&egrave;re qui peut para&icirc;tre h&eacute;sitant, c''est un homme qui aime rassembler les gens et pacifier les situations. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'balance', '1'),
(8, 'ambitieux et pers&eacute;v&eacute;rant qui aime mettre tous les atouts de son c&ocirc;t&eacute; afin d''arriver &agrave; ses fins. Son caract&egrave;re ind&eacute;pendant en ce qui concerne les d&eacute;cisions &agrave; prendre peut d&eacute;stabiliser certaines personnes du fait de son besoin d''exister en soci&eacute;t&eacute;. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'scorpion', '1'),
(9, 'dynamique, spontan&eacute; et ind&eacute;pendant qui a l''&acirc;me d''un leader et aime relever les d&eacute;fis qu''il rencontre quotidiennement. Bien qu''il ait de l''ambition et veut r&eacute;aliser tous ses r&ecirc;ves, il n''en demeure pas moins tr&egrave;s sociable. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'sagittaire', '1'),
(10, 'introverti, ind&eacute;pendant et pr&eacute;f&eacute;rant la solitude. Il peut para&icirc;tre froid au premier abord, mais est en r&eacute;alit&eacute; un homme charmant et sinc&egrave;re. Cette nature r&eacute;serv&eacute;e lui permet n&eacute;anmoins de prendre le temps de r&eacute;fl&eacute;chir aux situations quotidiennes et l''am&egrave;ne vers une certaine stabilit&eacute;. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'capricorne', '1'),
(11, 'g&eacute;n&eacute;reux, imaginatif et passionn&eacute; qui aime la vie en soci&eacute;t&eacute; et les relations amicales. Cette nature port&eacute;e vers l''humain le conduit &agrave; s''impliquer dans des projets visant &agrave; aider la communaut&eacute; dans son sens le plus g&eacute;n&eacute;ral. Mais il fait &eacute;galement preuve de sensibilit&eacute; bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'verseau', '1'),
(12, 'bienveillant et compatissant qui n''aime pas les situations conflictuelles. Dot&eacute; d''une grande imagination, il peut parfois para&icirc;tre dans les nuages alors qu''il fait preuve de beaucoup de psychologie et est tr&egrave;s r&eacute;fl&eacute;chi. Mais c''est &eacute;galement un homme sensible bien qu''il ne le montre pas toujours.', 'question_2', 'H', 'poissons', '1'),
(13, 'doux et sinc&egrave;re m&ecirc;me si il n''arrive pas vraiment &agrave; mettre en avant ses sentiments. Il fait souvent para&icirc;tre une image d''ind&eacute;pendance et d''ambition qui font de lui un homme assez difficile &agrave; cerner. Mais il est aussi un homme sensible bien qu''il ne le montre pas toujours.', 'question_2', 'H', '0', '0'),
(14, 'Ind&eacute;pendante et ambitieuse vous vous donnez les moyens d''obtenir ce que vous d&eacute;sirez. Vous aimez avoir une vie remplie d''aventures et foncez parfois t&ecirc;te baiss&eacute;e sans r&eacute;fl&eacute;chir.', 'question_2', 'F', 'belier', '1'),
